mapper.select:
SELECT b.* FROM ( SELECT a.*, rownum AS rownumber FROM ( SELECT LARGECATEGORYCD, LARGECATEGORYNAME, MIDDLECATEGORYCD, MIDDLECATEGORYNAME, SMALLCATEGORYCD, SMALLCATEGORYNAME, DESCRIPTION, ORDERNO, SYSTEMFLG, DELETEFLG, COUNT(*) OVER () COUNTNUMBER FROM ib_CODEMASTER WHERE deleteFlg = 0
AND (LARGECATEGORYNAME like '%'||#{keyValue}||'%' OR MIDDLECATEGORYNAME like '%'||#{keyValue}||'%' OR SMALLCATEGORYNAME like '%'||#{keyValue}||'%' )
ORDER BY ORDERNO ) a WHERE rownum <= #{endNumber jdbcType=INTEGER} ) b WHERE b.rownumber >= #{startNumber jdbcType=INTEGER}

mapper.update:
update ib_CODEMASTER ca
ca.largeCategoryCd = #{largeCategoryCd jdbcType=VARCHAR}, ca.largeCategoryName = #{largeCategoryName jdbcType=VARCHAR}, ca.middleCategoryCd = #{middleCategoryCd jdbcType=VARCHAR}, ca.middleCategoryName = #{middleCategoryName jdbcType=VARCHAR}, ca.smallCategoryCd = #{smallCategoryCd jdbcType=VARCHAR}, ca.smallCategoryName = #{smallCategoryName jdbcType=VARCHAR}, ca.description = #{description jdbcType=VARCHAR}, ca.orderNo = #{orderNo jdbcType=INTEGER}, ca.systemFlg = #{systemFlg jdbcType=INTEGER}
where ca.smallCategoryCd = #{smallCategoryCd}


update ib_CODEMASTER a



	a.deleteFlg = 1


where a.smallCategoryCd = #{smallCategoryCd}

mapper.select:
select cm.largeCategoryCd, cm.largeCategoryName, cm.middleCategoryCd, cm.middleCategoryName, cm.smallCategoryCd, cm.smallCategoryName, cm.orderNo from ib_codeMaster cm where cm.deleteFlg = 0
and cm.largeCategoryCd = #{largeCategoryCd}
and cm.middleCategoryCd = #{middleCategoryCd}
and cm.smallCategoryCd = #{smallCategoryCd}
order by cm.orderNo desc
